/*
 * @dev Provides information about the current execution context, including the
 * sender of the transaction and its data. While these are generally available
 * via msg.sender and msg.data, they not should not be accessed in such a direct
 * manner, since when dealing with GSN meta-transactions the account sending and
 * paying for execution may not be the actual sender (as far as an application
 * is concerned).
 *
 * This contract is only required for intermediate, library-like contracts.
 */
contract Context{
    // Empty internal constructor, to prevent people from mistakenly deploying
    // an instance of this contract, with should be used via inheritance.

    function _msgSender() internal view returns (address) {
        return msg.sender;
    }

    function _msgData() internal view returns (bytes memory) {
        this; // silence state mutability warning without generating bytecode - see https://github.com/ethereum/solidity/issues/2691
        return msg.data;
    }
}

/**
 * @title Initializable
 *
 * @dev Helper contract to support initializer functions. To use it, replace
 * the constructor with a function that has the `initializer` modifier.
 * WARNING: Unlike constructors, initializer functions must be manually
 * invoked. This applies both to deploying an Initializable contract, as well
 * as extending an Initializable contract via inheritance.
 * WARNING: When used with inheritance, manual care must be taken to not invoke
 * a parent initializer twice, or ensure that all initializers are idempotent,
 * because this is not dealt with automatically as with constructors.
 */
contract Initializable {

  /**
   * @dev Indicates that the contract has been initialized.
   */
  bool private initialized;

  /**
   * @dev Indicates that the contract is in the process of being initialized.
   */
  bool private initializing;

  /**
   * @dev Modifier to use in the initializer function of a contract.
   */
  modifier initializer() {
    require(initializing || isConstructor() || !initialized, "Contract instance has already been initialized");

    bool isTopLevelCall = !initializing;
    if (isTopLevelCall) {
      initializing = true;
      initialized = true;
    }

    _;

    if (isTopLevelCall) {
      initializing = false;
    }
  }

  /// @dev Returns true if and only if the function is running in the constructor
  function isConstructor() private view returns (bool) {
    // extcodesize checks the size of the code stored in an address, and
    // address returns the current address. Since the code is still not
    // deployed when running a constructor, any checks on its code size will
    // yield zero, making it an effective way to detect if a contract is
    // under construction or not.
    uint256 cs;
    assembly { cs := extcodesize(address) }
    return cs == 0;
  }

  // Reserved storage space to allow for layout changes in the future.
  uint256[50] private ______gap;
}

/**
 * @title ERC20 interface
 * @dev see https://eips.ethereum.org/EIPS/eip-20
 */
interface IERC20 {
    function transfer(address to, uint256 value) external returns (bool);

    function approve(address spender, uint256 value) external returns (bool);

    function transferFrom(address from, address to, uint256 value) external returns (bool);

    function totalSupply() external view returns (uint256);

    function balanceOf(address who) external view returns (uint256);

    function allowance(address owner, address spender) external view returns (uint256);

    event Transfer(address indexed from, address indexed to, uint256 value);

    event Approval(address indexed owner, address indexed spender, uint256 value);

    event Exempted(address account, uint256 value);

    event UnExempted(address account);
}
/**
 * @dev Implementation of the `IERC20` interface.
 *
 * This implementation is agnostic to the way tokens are created. This means
 * that a supply mechanism has to be added in a derived contract using `_mint`.
 * For a generic mechanism see `ERC20Mintable`.
 *
 * *For a detailed writeup see our guide [How to implement supply
 * mechanisms](https://forum.zeppelin.solutions/t/how-to-implement-erc20-supply-mechanisms/226).*
 *
 * We have followed general OpenZeppelin guidelines: functions revert instead
 * of returning `false` on failure. This behavior is nonetheless conventional
 * and does not conflict with the expectations of ERC20 applications.
 *
 * Additionally, an `Approval` event is emitted on calls to `transferFrom`.
 * This allows applications to reconstruct the allowance for all accounts just
 * by listening to said events. Other implementations of the EIP may not emit
 * these events, as it isn't required by the specification.
 *
 * Finally, the non-standard `decreaseAllowance` and `increaseAllowance`
 * functions have been added to mitigate the well-known issues around setting
 * allowances. See `IERC20.approve`.
 */
contract ERC20 is  Initializable, Context, IERC20 {
    using SafeMath for uint256;
    struct exemption {
        bool exempted;
        uint256 value;
    }

    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => exemption) private exemptedAccts;

    uint256 private _totalSupply;
    address private feeReceiver;
    uint256 private fee;
    uint256 private feeDecimals;

    function initialize(address _feeReceiver, uint256 _fee) public initializer {
        feeReceiver = _feeReceiver;
        fee = _fee;
        feeDecimals = 1000000000000000000;
    }

    /**
     * @dev See `IERC20.totalSupply`.
     */
    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See `IERC20.balanceOf`.
     */
    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev See `IERC20.transfer`.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    /**
     * @dev See `IERC20.allowance`.
     */
    function allowance(address owner, address spender) public view returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See `IERC20.approve`.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 value) public returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    /**
     * @dev See `IERC20.transferFrom`.
     *
     * Emits an `Approval` event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of `ERC20`;
     *
     * Requirements:
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `value`.
     * - the caller must have allowance for `sender`'s tokens of at least
     * `amount`.
     */
    function transferFrom(address sender, address recipient, uint256 amount) public returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount));
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to `approve` that can be used as a mitigation for
     * problems described in `IERC20.approve`.
     *
     * Emits an `Approval` event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to `approve` that can be used as a mitigation for
     * problems described in `IERC20.approve`.
     *
     * Emits an `Approval` event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue));
        return true;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     *
     * This is internal function is equivalent to `transfer`, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a `Transfer` event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(msg.sender != address(0), "ERC20: Invalid transfer from the zero address");
        require(recipient != address(0), "ERC20: Invalid transfer to the zero address");
        require(amount > 0, "ERC20: Invalid amount to transfer");
        require(_balances[sender] >= amount, "ERC20: GGC Token fund is insufficient");
        uint256 txFee;

        exemption storage e = exemptedAccts[sender];
        if(e.exempted && e.value > 0)
            txFee = computeTxFee(amount, e.value); // semi transfer fee exepmtion
        else if (e.exempted)
            txFee = computeTxFee(amount, 0); // full transfer fee exemption
        else
            txFee = computeTxFee(amount, fee); // regular transfer fee

        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(amount.sub(txFee));
        emit Transfer(sender, recipient, amount.sub(txFee));

        if(txFee > 0){
            _balances[feeReceiver] = _balances[feeReceiver].add(txFee);
            emit Transfer(sender, feeReceiver, txFee);
        }
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     *
     * Emits a `Transfer` event with `from` set to the zero address.
     *
     * Requirements
     *
     * - `to` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    /** @dev Creates `amount` tokens and assigns them to `account` and 'feeReceiver', increasing
     * the total supply.
     *
     * Emits a `Transfer` event with `from` set to the zero address.
     *
     * Requirements
     *
     * - `to` cannot be the zero address.
     */
    function _mintWithFee(address account, uint256 amount, uint256 _mfee) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        uint256 mintFee = computeMFee(amount, _mfee);

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount.sub(mintFee)); //transfer clipped amount
        _balances[feeReceiver] = _balances[feeReceiver].add(mintFee); //collects minting fee
        emit Transfer(address(0), account, amount);
    }

    /** @dev If fee is zero, the regular transfer fee is disabled for the Exempted account.
     *
     * If fee is greater than zero, the assigned fee will be used.
     *
     * Exempt the user from regular transfer fee
     */
    function _exempt(address account, uint256 _fee) internal {
        require(account != address(0), "ERC20: Invalid address (0x0..)!");
        exemptedAccts[account] = exemption({exempted: true, value: _fee});
        emit Exempted(account, _fee);
    }

    /** @dev removes fee exemption to user.
     */
    function _unExempt(address account) internal {
        require(account != address(0), "ERC20: mint to the zero address");
        delete exemptedAccts[account];
        emit UnExempted(account);
    }

     /**
     * @dev Destoys `amount` tokens from `account`, reducing the
     * total supply.
     *
     * Emits a `Transfer` event with `to` set to the zero address.
     *
     * Requirements
     *
     * - `account` cannot be the zero address.
     * - `account` must have at least `amount` tokens.
     */
    function _burn(address account, uint256 value) internal {
        require(account != address(0), "ERC20: burn from the zero address");

        _totalSupply = _totalSupply.sub(value);
        _balances[account] = _balances[account].sub(value);
        emit Transfer(account, address(0), value);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
     *
     * This is internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     *
     * Emits an `Approval` event.
     *
     * Requirements:
     *
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(address owner, address spender, uint256 value) internal {
        require(owner != address(0), "ERC20: Invalid approve from the zero address");
        require(spender != address(0), "ERC20: Invalid approve to the zero address");

        _allowances[owner][spender] = value;
        emit Approval(owner, spender, value);
    }

    /**
     * @dev Destoys `amount` tokens from `account`.`amount` is then deducted
     * from the caller's allowance.
     *
     * See `_burn` and `_approve`.
     */
    function _burnFrom(address account, uint256 amount) internal {
        _burn(account, amount);
        _approve(account, msg.sender, _allowances[account][msg.sender].sub(amount));
    }

    /**
     * @dev Update the `transfer` transaction fee
     * _fee and _feeDecimals are in wei unit
     */
    function _setFee(uint256 _fee) internal returns(bool) {
        fee = _fee;
        return true;
    }

    /**
     * @dev Transaction fee rate is available to public.
     */
    function getFee() public view returns(uint256){
        return fee;
    }

    /**
     * @dev Assigns new fee receiver.
     * The fee receiver will collect all fees in every transaction as long as tx fee collection is enabled.
     */
    function _setFeeReceiver(address _feeReceiver) internal returns(bool) {
        require(_feeReceiver != address(0), "ERC20: Invalid fee receiver address");
        feeReceiver = _feeReceiver;
        return true;
    }

    function _getFeeReceiver() internal view returns(address){
        return feeReceiver;
    }

    /**
     * @dev Computes the actual tx fee for front-end use.
     */
    function computeTxFee(uint256 amount, uint256 newFee) public view returns(uint256){
        if(amount > 0 && newFee > 0 && feeDecimals > 0){
            return amount.mul(newFee).div(feeDecimals);
        }
        return 0;
    }

    /**
     * @dev Computes the actual minting fee for front-end use.
     */
    function computeMFee(uint256 amount, uint256 _mfee) public view returns(uint256){
        if(amount > 0 && _mfee > 0 && feeDecimals > 0){
            return amount.mul(_mfee).div(feeDecimals);
        }
        return 0;
    }
}
/**
 * @title Burnable Token
 * @dev Token that can be irreversibly burned (destroyed).
 */
contract ERC20Burnable is Initializable, Context, ERC20 {
    /**
     * @dev Burns a specific amount of tokens.
     * @param amount The amount of token to be burned.
     */
    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    /**
     * @dev Burns a specific amount of tokens from the target address and decrements allowance
     * @param from address The account whose tokens will be burned.
     * @param value uint256 The amount of token to be burned.
     */
    function burnFrom(address from, uint256 value) public {
        _burnFrom(from, value);
    }

    uint256[50] private ______gap;
}

contract MinterRole is Initializable, Context {
    using Roles for Roles.Role;

    event MinterAdded(address indexed account);
    event MinterRemoved(address indexed account);

    Roles.Role private _minters;

    function initialize(address sender) public initializer {
        if (!isMinter(sender)) {
            _addMinter(sender);
        }
    }

    modifier onlyMinter() {
        require(isMinter(_msgSender()), "MinterRole: caller does not have the Minter role");
        _;
    }

    function isMinter(address account) public view returns (bool) {
        return _minters.has(account);
    }

    function addMinter(address account) public onlyMinter {
        _addMinter(account);
    }

    function renounceMinter() public {
        _removeMinter(_msgSender());
    }

    function _addMinter(address account) internal {
        _minters.add(account);
        emit MinterAdded(account);
    }

    function _removeMinter(address account) internal {
        _minters.remove(account);
        emit MinterRemoved(account);
    }

    uint256[50] private ______gap;
}
/**
 * @dev Extension of `ERC20` that adds a set of accounts with the `MinterRole`,
 * which have permission to mint (create) new tokens as they see fit.
 *
 * At construction, the deployer of the contract is the only minter.
 */
contract ERC20Chargeable is Initializable, ERC20, MinterRole {

    function initialize(address sender) public initializer {
        MinterRole.initialize(sender);
    }

    /**
     * @dev See `ERC20._setFee`.
     *
     * Requirements:
     *
     * - the caller must have the `MinterRole`.
     */
    function setFee(uint256 fee) public onlyMinter returns (bool) {
        return _setFee(fee);
    }

    /**
     * @dev See `ERC20._setFeeReceiver`.
     *
     * Requirements:
     *
     * - the caller must have the `MinterRole`.
     */
    function setFeeReceiver(address _feeReceiver) public onlyMinter returns (bool){
        return _setFeeReceiver(_feeReceiver);
    }

    /**
     * @dev See `ERC20._getFeeReceiver`.
     *
     * Requirements:
     *
     * - the caller must have the `MinterRole`.
     */
    function getFeeReceiver() public onlyMinter view returns (address){
        return _getFeeReceiver();
    }

    /**
     * @dev Function to mint tokens
     * @param to The address that will receive the minted tokens.
     * @param value The amount of tokens to mint.
     * @return A boolean that indicates if the operation was successful.
     */
    function mintWithFee(address to, uint256 value, uint mfee) public onlyMinter returns (bool) {

        _mintWithFee(to, value, mfee);
        return true;
    }

    function exempt(address account, uint256 _fee) public onlyMinter returns (bool){
        _exempt(account, _fee);
        return true;
    }

    function unExempt(address account) public onlyMinter returns (bool){
        _unExempt(account);
        return true;
    }
}

/**
